INSERT INTO "regent" VALUES ('010POLBCPV73SSOLRSGKLGDGEK', '000', '本底', '本底', 100, '0号磁片');
INSERT INTO "regent" VALUES ('010POLBCSDEIQCULRSGKNU3OUT', 111, 'dsDNA', '抗双链DNA抗体', 101, '是系统性红斑狼疮的特异性抗体 ，且与SLE的活动性 ，尤其是狼疮肾炎的活动性密切相关。在SLE中阳性率为30%~90%;在活动期SLE中的阳性率为80%~100%。是系统性红斑狼疮的诊断标准之一。抗双链DNA抗体与抗Clq抗体联合检测为活动性狼疮肾炎的特异性检测指标 ，两种抗体同时阴性可排除活动性狼疮肾炎');
INSERT INTO "regent" VALUES ('010POLBCV2NSSUKLRSGN23PCHT', '031', 'Nucleosome', '抗核小体抗体', 102, '系统性红斑狼疮的特异性抗体 ， 阳性率达40%~70% , 特异性大于98% , 有研究显示 ， 该抗体是比抗双链DNA抗体、 抗组蛋白抗体更敏感的SLE标记物 ， 更早出现于SLE的早期 ， 被认为是SLE的标记性抗体');
INSERT INTO "regent" VALUES ('010POLBD1P71D1ALRSGL8320VG', '032', 'Histone', '抗组蛋白抗体', 103, '此抗体不具有特异性 ， 可出现在多种自身免疫性疾病（DIL SLE、 RA , SSc ) 中 ，但在药物性红斑狼疮（DIL ) 中的阳性率达到95% ,另外也见于30%-70%的非药物性 狼疮以及15%-50%的类风湿关节炎患者');
INSERT INTO "regent" VALUES ('010POLBD4QBHQAOLRSGKDAPHF2', '004', 'CENP-B', '抗着丝点抗体', 104, '抗着丝点抗体对局限型进行性系统性硬化症具有很高的特异性和敏感性 ， 阳性率为70%~90%。 诊断抗着丝点抗体对于监测局限型系统性硬化症具有重要的临床意义。');
INSERT INTO "regent" VALUES ('010POLBD7ORGBV4LRSGN540S3L', '006', 'Sm', '抗史密斯抗体', 105, '是系统性红斑狼疮的标志性抗体 ， 特异性达95%。 但灵敏度较低5%~30% ,该抗体的浓度可能与SLE的活动相关 ， 浓度升高可能预示着SLE的复发 ， 对早期、 不典型的SLE或经治疗后SLE的回顾性诊断有很大帮助');
INSERT INTO "regent" VALUES ('010POLBD9RFU24MLRSGNDUQG0H', '007', 'P0', '抗核糖体P蛋白抗体', 106, '系统性红斑狼疮的高度特异性指标。 检出率为5%~20% , SLE患者出现此抗体与中枢神经系统（脑神经在SLE受累率为5%~33% ) 、肝脏或肾脏受累相关 ； 对具有脑炎和精神症状的SLE患者 ， 抗核糖体抗体的敏感性为56%~90%。');
INSERT INTO "regent" VALUES ('010POLBDBVEAD0ULRSGLF49TAG', '008', 'SS-A/Ro 52kD', 'SS-A/Ro 52kD', 107, '干燥综合征的标志性抗体 ， 均见于SS和SLE。但单独出现Ro52kD更多见于原发性干燥综合征（pSS ) 中 ， 而只出现Ro60kD , 更多见于SLE , 尤其是亚急性皮肤型狼疮（SCLE )和新生儿狼疮综合征（NLE ) , 其发生率几乎为100%');
INSERT INTO "regent" VALUES ('010POLBDE8AHPKQLRSGLK2CGQH', '009', 'SS-A/Ro 60kD', 'SS-A/Ro 60kD', 108, '干燥综合征的标志性抗体 ， 均见于SS和SLE。但单独出现Ro52kD更多见于原发性干燥综合征（pSS ) 中 ， 而只出现Ro60kD , 更多见于SLE , 尤其是亚急性皮肤型狼疮（SCLE )和新生儿狼疮综合征（NLE ) , 其发生率几乎为100%');
INSERT INTO "regent" VALUES ('010POLBDGDT9KVQLRSGMHH9RU1', '010', 'SS-B/La', 'SS-B/La', 109, '干燥综合征的高度特异性抗体。');
INSERT INTO "regent" VALUES ('010POLBDIR3BIUULRSGL007HA7', 113, 'PCNA', '抗增殖细胞核抗原抗体', 110, '系统性红斑狼疮的高度特异性抗体 ， 可作为SLE的标记性抗体 ， 阳性率为1%~5%。');
INSERT INTO "regent" VALUES ('010POLBDLVR9JTULRSGMC567TM', '033', 'AMA M2', '抗线粒体抗体M2抗体', 111, '高浓度的M2型抗体是诊断原发性胆汁性肝硬化（PBC ) 的高度特异性指标 ， 阳性率为94%。 抗AMA-M2抗体量值的高低对疾病的预后意义明确 ， 也是原发性胆汁性肝硬化和肝外胆道阻塞性肝硬化的鉴别诊断依据');
INSERT INTO "regent" VALUES ('010POLBDO9G0S54LRSGLPUT73M', '013', 'Scl-70', '抗Scl70抗体', 112, '系统性硬化症的特异性抗体 ， 特异性为100% ,敏感性为40%。 该抗体阳性与弥漫性皮肤改变、 近端皮肤累及、 肺间质纤维化、 心脏、 肾脏等多器官受累密切相关 ，被视为预后不良的指标。');
INSERT INTO "regent" VALUES ('010POLBDQOV4OTULRSGMN5VMFR', '014', 'Ku', '抗Ku抗体', 113, '许多自身免疫疾病发现有抗Ku抗体 ， 包括多发性肌炎、 狼疮及硬皮病 ， 见于1-7%的肌炎患者。 抗Ku抗体检测阳性可以提示有发展为肌炎的高度危险。');
INSERT INTO "regent" VALUES ('010POLBDTCAK3BALRSGMU9BM0C', '016', 'Mi-2', '抗Mi-2抗体', 114, '可在10-15%的急性皮肌炎患者中检测到，诊断特异性大于 96%。');
INSERT INTO "regent" VALUES ('010POLBE00CFOS2LRSGLMTKTBH', '017', 'Jo-1', '抗Jo-1抗体', 115, '对多发性肌炎/皮肌炎有高度特异性（>95% ) ， 在合并肺间质病变的患者中其阳性率可达60%。 效价与疾病活动程度相关 ， 有效定量检测是肌炎诊断和疗效观察 ，早期诊断伴发肺纤维化的指标');
INSERT INTO "regent" VALUES ('010POLBE2IG58DGLRSGKJ3N077', '003', 'PM-Scl', '抗PM-Scl抗体', 116, '常见于多肌炎与硬化症的重叠综合征中 ， 阳性率为50% , 也可以仅出现于多肌炎患者中 ， 阳性率为8%, 在弥散性硬化症中的阳性率为2%~5%。');
INSERT INTO "regent" VALUES ('010POLBE5NRHG8CLRSGNGD8V0T', '015', 'U1-snRNP', '抗U1-snRNP抗体', 117, '是混合结缔组织病（MTCD ) 的标记抗体。在抗Sm抗体和抗dsDNA抗体阴性的情况下 ， 该抗体的灵敏度可以达100% , 特异性可达98%。');
INSERT INTO "regent" VALUES ('010POLBE8ILT65ILRSGKM0BRUK', '018', 'MPO', '抗髓过氧化物酶抗体', 201, '可见于多种系统性血管炎 ， 与MPA、 NCGN、CSS相关。 该抗体浓度与病情严重程度和治疗效果相关 ， 定量检测该抗体浓度可作为判断疗效、 评估疾病复发的指标');
INSERT INTO "regent" VALUES ('010POLBEBG9VCGILRSGLRD0TF2', 116, 'PR3', '抗蛋白酶3抗体', 202, '是系统性血管炎的诊断与鉴别诊断指标 ， 主要见于韦格纳肉芽肿（WG ) , 对WG有很高的特异性。 该抗体水平与WG的病情严重程度和治疗效果相关 ， 定量检测该抗体浓度可作为判断疗效、 评估疾病复发的指标。');
INSERT INTO "regent" VALUES ('010POLBEE53MMOELRSGM5I9PRA', 118, 'GBM', '抗肾小球基底膜抗体', 203, '对于怀疑原发性系统性小血管炎的患者 ， 除检测ANCA外 ， 还应该检测GBM抗体。 抗GBM抗体是抗GBM抗体型肾炎的特异性抗体 ， 在肺出血-肾炎综合征（Goodpasture syndrome) 中几乎100%阳性 ；与疾病活动性相关 ， 可用于监测疾病进程及疗效评估');
